sys_script_client.script:
function onCellEdit(sysIDs, table, oldValues, newValue, callback) {

	var ga=new GlideAjax('cellEditGuide');
	ga.addParam('sysparm_name','validateDates');
	ga.addParam('sysparm_sysid',sysIDs);
	ga.addParam('sysparm_newVlaue',newValue);
	ga.getXML(callscriptinclude);


	function callscriptinclude(response){
		var answer =  response.responseXML.documentElement.getAttribute("answer");

		if(answer=="do not submit"){
			alert("please enter issue date smaller than resolved date");
			callback(false);

		}
		else if(answer=="submit it")
		{
		var confirmation =confirm("are you sure want to change the issue date?");

		callback(confirmation);
		}
	}
	}
