sys_script_include.script:
var cellEditGuide = Class.create();
cellEditGuide.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	validateDates :function(){

	var newDate=this.getParameter('sysparm_newValue');
	var gr=new GlideRecord('incident');
		gr.addQuery('sys_id',this.getParameter('sysyparm_sysid'));
	gr.query();
		if(gr.next()){
			if(newDate>gr.issue_dated_on){
				return "do not submit";
			}
			else if (newDate<=gr.issue_resolved_on)
				{
					return "submit it";
				}
		}

	},

	type: 'cellEditGuide'
});
